    void carregarDados(){
        DBController db = new DBController("jogo_luta.db");
        if(!db.conectar()){
            JOptionPane.showMessageDialog(null,"Arquivo n√£o encontrado");
            return;
        }
        
        ResultSet rset = db.carregarLutadores();
        if(rset == null){
            JOptionPane.showMessageDialog(null,"Erro ao carregar dados");
        }
        
        DefaultTableModel model = new DefaultTableModel(new String[]{"Lutador","Nacionalidade","Categoria","Vitorias","Derrotas","Empates"},0);
        try{
            while(rset.next()){
                model.addRow(new Object[]{ 
                                           rset.getString("nome"),
                                           rset.getString("nacionalidade"),
                                           rset.getString("categoria"),
                                           rset.getString("vitorias"),
                                           rset.getString("derrotas"),
                                           rset.getString("empates")
                                           
                                           
                });
            }
        }catch(Exception e){
            JOptionPane.showMessageDialog(null,"Erro ao listar dados");
        }
        
        tbl_lutadores.setModel(model);
        
        db.desconectar();
    }
    
    
    
